using System.Collections;using System.Collections.Generic;using UnityEngine;public class Poolable : MonoBehaviour {    public float autoReturn = Mathf.Infinity;    public IPool motherpool;    public virtual void Start() {        Reactivate();    }    public virtual void Deactivate() {        gameObject.SetActive(false);    }    public virtual void Reactivate() {        if (autoReturn < Mathf.Infinity)            Invoke("Return", autoReturn);        else            Debug.Log("asdf.");    }    public virtual void Return() {        if (motherpool == null)            Debug.LogError("Return on Poolable called, but " + gameObject.name + " has no motherpool.");        else            motherpool.Return(this);    }}