using System.Collections;using System.Collections.Generic;using UnityEngine;[ExecuteInEditMode]public class LerpTo : MonoBehaviour {    public Vector3 offset;    public float smoothing;    public Transform target;    protected static Vector3 ObjectRelativeOffset(Vector3 offset, Vector3 objectForward) {        float angle = Vector3.Angle(objectForward, Vector3.forward);        return Quaternion.AngleAxis(angle, Vector3.up) * offset;    }    protected void LerpPos() {        Vector3 lerped = Vector3.Slerp(            gameObject.transform.position,            target.position + ObjectRelativeOffset(offset, target.forward),            smoothing * Time.deltaTime        );        gameObject.transform.position = lerped;    }    protected void LateUpdate() {        LerpPos();    }}