using System.Collections;using System.Collections.Generic;using UnityEngine;public class SpinLikeATop : MonoBehaviour {    public float speed;    public float wobbleSpeed;    public float wobbleAngle;    protected float t;    protected float originalX;    public void Start() {        originalX = transform.rotation.eulerAngles.x;    }    public void Update () {        t += Time.deltaTime * wobbleSpeed;        Vector3 rot = transform.rotation.eulerAngles;        rot.y += speed * Time.deltaTime;        rot.x = Mathf.Sin(t) * wobbleAngle + originalX;        transform.rotation = Quaternion.Euler(rot);    }}